mapper.update:
UPDATE user
SET password=#{password},
nickname=#{nickname},
isWithDraw=#{isWithDraw},
status=#{status}
WHERE id = #{id}

mapper.select:
SELECT id, name, nickname, password, status, isWithDraw, createdAt, updatedAt
FROM user
WHERE userId = #{id}
AND passWord = #{password}
AND status != 'DELETED'

SELECT id, name, nickname, password, status, isWithDraw, createdAt, updatedAt
FROM user
WHERE nickname = #{nickname}
AND passWord = #{password}
AND status != 'DELETED'

SELECT COUNT(id)
FROM user
WHERE nickname = #{nickname}

mapper.update:
UPDATE user
SET passWord = #{password}
WHERE id = #{id}
